sqlMap.select:
WITH CREDIT_LINE_INVOICE AS ( SELECT ( CASE WHEN CREDIT.MAST_TABLE_NAME = 'CUST_AGENT_LEVEL_CRD' THEN ( SELECT DECODE(COUNT(1), 0, 'Y', 'N') FROM CUST_AGENT_LEVEL_CRD WHERE SEQ_NO = CREDIT.MAST_SEQ_NO AND CR_EFF_DATE_TO >= TO_DATE(TO_CHAR(SYSDATE, 'DDMONYY'), 'DDMONYY') ) WHEN CREDIT.MAST_TABLE_NAME = 'CUST_AGENT_AIRL_CREDIT' THEN ( SELECT DECODE(COUNT(1), 0, 'Y', 'N') FROM CUST_AGENT_AIRL_CREDIT WHERE SEQ_NO = CREDIT.MAST_SEQ_NO AND CR_EFF_DATE_TO >= TO_DATE(TO_CHAR(SYSDATE, 'DDMONYY'), 'DDMONYY') ) WHEN CREDIT.MAST_TABLE_NAME = 'CUST_AGENT_CREDIT' THEN ( SELECT DECODE(COUNT(1), 0, 'Y', 'N') FROM CUST_AGENT_CREDIT WHERE SEQ_NO = CREDIT.MAST_SEQ_NO AND CR_EFF_DATE_TO >= TO_DATE(TO_CHAR(SYSDATE, 'DDMONYY'), 'DDMONYY') ) ELSE 'Y' END ) creditLineExpired FROM BILL_INV_CRD_DTL CREDIT JOIN BILL_INV_DTL INVOICE ON INVOICE.INV_NO = CREDIT.INV_NO AND INVOICE.DOC_LINE_NO = CREDIT.INV_DOC_LINE_NO AND INVOICE.CNSLDT_INV_NO = #CONSOLIDATED_INVOICE_NO#
AND CREDIT.SEQ_NO = #SEQ_NO#
), CREDIT_LINE_ADJUST AS ( SELECT ( CASE WHEN CREDIT.MAST_TABLE_NAME = 'CUST_AGENT_LEVEL_CRD' THEN ( SELECT DECODE(COUNT(1), 0, 'Y', 'N') FROM CUST_AGENT_LEVEL_CRD WHERE SEQ_NO = CREDIT.MAST_SEQ_NO AND CR_EFF_DATE_TO >= TO_DATE(TO_CHAR(SYSDATE, 'DDMONYY'), 'DDMONYY') ) WHEN CREDIT.MAST_TABLE_NAME = 'CUST_AGENT_AIRL_CREDIT' THEN ( SELECT DECODE(COUNT(1), 0, 'Y', 'N') FROM CUST_AGENT_AIRL_CREDIT WHERE SEQ_NO = CREDIT.MAST_SEQ_NO AND CR_EFF_DATE_TO >= TO_DATE(TO_CHAR(SYSDATE, 'DDMONYY'), 'DDMONYY') ) WHEN CREDIT.MAST_TABLE_NAME = 'CUST_AGENT_CREDIT' THEN ( SELECT DECODE(COUNT(1), 0, 'Y', 'N') FROM CUST_AGENT_CREDIT WHERE SEQ_NO = CREDIT.MAST_SEQ_NO AND CR_EFF_DATE_TO >= TO_DATE(TO_CHAR(SYSDATE, 'DDMONYY'), 'DDMONYY') ) ELSE 'Y' END ) creditLineExpired FROM BILL_INV_CRD_DTL CREDIT JOIN BILL_ADJ_DTL ADJUST ON ADJUST.ADJ_NO = CREDIT.ADJ_NO AND ADJUST.DOC_LINE_NO = CREDIT.ADJ_DOC_LINE_NO AND ADJUST.CNSLDT_INV_NO = #CONSOLIDATED_INVOICE_NO#
AND CREDIT.SEQ_NO = #SEQ_NO#
) SELECT DECODE(COUNT(1), 0, 'N', 'Y') FROM ( SELECT 1 FROM CREDIT_LINE_INVOICE WHERE creditLineExpired = 'Y' UNION SELECT 1 FROM CREDIT_LINE_ADJUST WHERE creditLineExpired = 'Y' )

WITH CREDIT_LINE_INVOICE AS ( SELECT ( CASE WHEN CREDIT.MAST_TABLE_NAME = 'CUST_AGENT_LEVEL_CRD' THEN ( SELECT DECODE(COUNT(1), 0, 'N', 'Y') FROM CUST_AGENT_LEVEL_CRD WHERE SEQ_NO = CREDIT.MAST_SEQ_NO AND NVL(STATUS, 'OK') = 'VOID' ) WHEN CREDIT.MAST_TABLE_NAME = 'CUST_AGENT_AIRL_CREDIT' THEN ( SELECT DECODE(COUNT(1), 0, 'N', 'Y') FROM CUST_AGENT_AIRL_CREDIT WHERE SEQ_NO = CREDIT.MAST_SEQ_NO AND NVL(STATUS, 'OK') = 'VOID' ) WHEN CREDIT.MAST_TABLE_NAME = 'CUST_AGENT_CREDIT' THEN ( SELECT DECODE(COUNT(1), 0, 'N', 'Y') FROM CUST_AGENT_CREDIT WHERE SEQ_NO = CREDIT.MAST_SEQ_NO AND NVL(STATUS, 'OK') = 'VOID' ) ELSE 'N' END ) creditLineDeleted FROM BILL_INV_CRD_DTL CREDIT JOIN BILL_INV_DTL INVOICE ON INVOICE.INV_NO = CREDIT.INV_NO AND INVOICE.DOC_LINE_NO = CREDIT.INV_DOC_LINE_NO AND INVOICE.CNSLDT_INV_NO = #CONSOLIDATED_INVOICE_NO#
AND CREDIT.SEQ_NO = #SEQ_NO#
), CREDIT_LINE_ADJUST AS ( SELECT ( CASE WHEN CREDIT.MAST_TABLE_NAME = 'CUST_AGENT_LEVEL_CRD' THEN ( SELECT DECODE(COUNT(1), 0, 'N', 'Y') FROM CUST_AGENT_LEVEL_CRD WHERE SEQ_NO = CREDIT.MAST_SEQ_NO AND NVL(STATUS, 'OK') = 'VOID' ) WHEN CREDIT.MAST_TABLE_NAME = 'CUST_AGENT_AIRL_CREDIT' THEN ( SELECT DECODE(COUNT(1), 0, 'N', 'Y') FROM CUST_AGENT_AIRL_CREDIT WHERE SEQ_NO = CREDIT.MAST_SEQ_NO AND NVL(STATUS, 'OK') = 'VOID' ) WHEN CREDIT.MAST_TABLE_NAME = 'CUST_AGENT_CREDIT' THEN ( SELECT DECODE(COUNT(1), 0, 'N', 'Y') FROM CUST_AGENT_CREDIT WHERE SEQ_NO = CREDIT.MAST_SEQ_NO AND NVL(STATUS, 'OK') = 'VOID' ) ELSE 'N' END ) creditLineDeleted FROM BILL_INV_CRD_DTL CREDIT JOIN BILL_ADJ_DTL ADJUST ON ADJUST.ADJ_NO = CREDIT.ADJ_NO AND ADJUST.DOC_LINE_NO = CREDIT.ADJ_DOC_LINE_NO AND ADJUST.CNSLDT_INV_NO = #CONSOLIDATED_INVOICE_NO#
AND CREDIT.SEQ_NO = #SEQ_NO#
) SELECT DECODE(COUNT(1), 0, 'N', 'Y') FROM ( SELECT 1 FROM CREDIT_LINE_INVOICE WHERE creditLineDeleted = 'Y' UNION SELECT 1 FROM CREDIT_LINE_ADJUST WHERE creditLineDeleted = 'Y' )
